mapper.select:
SELECT MAX(attendance) AS maxAttendance
FROM lecture_applly
WHERE ilecture = #{ilecture};

SELECT midterm_examination AS maxMidtermExamination
FROM lecture_applly
WHERE ilecture = #{ilecture};

SELECT MAX(final_examination) AS maxFinalExamination
FROM lecture_applly
WHERE ilecture = #{ilecture};

SELECT B.student_num studentNum, B.nm, C.major_name majorName, A.attendance, A.midterm_examination midtermExamination, A.final_examination finalExamination FROM lecture_student A INNER JOIN student B ON A.istudent = B.istudent INNER JOIN major C ON B.imajor = C.imajor INNER JOIN lecture_applly D ON A.ilecture = D.ilecture
D.iprofessor = #{iprofessor}
AND A.ilecture = #{ilecture}
AND B.student_num = #{studentNum}
LIMIT #{staIdx}, 10;
